function closeToast() {
  const toastContainer = document.querySelector(".toast-position");
  toastContainer.classList.remove("toast-closed");
  toastContainer.classList.add("toast-visible");

  setTimeout(() => {
    autoCloseToast();
  }, 3000);
}

function autoCloseToast() {
  const toastContainer = document.querySelector(".toast-position");
  toastContainer.classList.remove("toast-visible");
  toastContainer.classList.add("toast-closed");
}
